import { VApp, VAppBar, VContainer, VRow, VCol, VCard, VCardTitle, VCardText, VSlideYTransition } from 'vuetify';
export default {
  name: 'AppHeaderSection',
  components: {
    VApp,
    VAppBar,
    VContainer,
    VRow,
    VCol,
    VCard,
    VCardTitle,
    VCardText,
    VSlideYTransition
  },
  computed: {
    headerCardClass() {
      return {
        'headline-md': this.$vuetify.breakpoint.mdAndUp,
        'headline-sm-and-down': this.$vuetify.breakpoint.smAndDown,
        'body-md': this.$vuetify.breakpoint.mdAndUp,
        'body-sm-and-down': this.$vuetify.breakpoint.smAndDown
      };
    }
  }
};
